import axios from 'axios';

import React, { Component } from 'react';
import { Button, Form, Row, Col, Table, FormGroup } from 'react-bootstrap';

import API_ENDPOINT from "./api/endpoint"

class App extends Component {

    constructor(props) {
        super(props);
        this.state = { selectedFile: null, password: "", responseMsg: "", value: 0 };
    }

    componentDidMount = () => {
        fetch(`${API_ENDPOINT}/settings`, met).then(res => res.json())
        .then(res => console.log(res))
    }

    onValueChange = (e) => this.setState({ password: e.target.value });

    onPasswordChange = (e) => this.setState({ password: e.target.value });

    onFileChange = (e) => this.setState({ selectedFile: e.target.files[0] });

    onFileUpload = () => {

        const formData = new FormData();

        formData.append(
            "uploadedFile",
            this.state.selectedFile,
            this.state.selectedFile.name,
        );

        formData.append("password", this.state.password)

        fetch(`${API_ENDPOINT}/settings`, { method: "POST", body: formData })
            .then(res => res.json())
            .then(res => this.setState({ responseMsg: res.msg }))
            .catch(err => this.setState({ responseMsg: err }));

    };



    render() {

        return (
            <div className="parent">
                <Row>
                    <Col>
                        <Table className="grid-item" striped bordered hover>
                            <thead>
                                <tr>
                                    <th>Variable</th>
                                    <th>Value</th>

                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td>HCAO</td>
                                    <td>1.14</td>

                                </tr>
                                <tr>
                                    <td>HCAD</td>
                                    <td>0.986</td>
                                </tr>
                                <tr>
                                    <td>Last Updated HC</td>
                                    <td>12-19-2020 22:00:00 CST</td>
                                </tr>
                                <tr>
                                    <td>Last Updated CSV</td>
                                    <td>12-19-2020 22:00:00 CST</td>
                                </tr>
                            </tbody>
                        </Table>
                    </Col>
                    <Col>
                        <Form className="grid-item">
                            <h1 className="mb-5">Edit Settings</h1>

                            <input className="mb-5" type="file" accept=".csv" onChange={this.onFileChange} />

                            <Form.Group controlId="formBasicPassword">
                                <Form.Label>HCAO</Form.Label>
                                <Form.Control type="text" placeholder={this.state.password} onChange={this.onPasswordChange} />
                            </Form.Group>

                            <Form.Group controlId="formBasicPassword">
                                <Form.Label>Password</Form.Label>
                                <Form.Control type="password" placeholder={this.state.password} onChange={this.onPasswordChange} />
                            </Form.Group>
                            <Button className="mb-5" onClick={this.onFileUpload}>
                                Upload!
                    </Button>

                        </Form>
                    </Col>
                </Row>




                <style global jsx>{`
        .parent {
            display: flex;
            justify-content: center;
            
        }



        .grid-item {
            margin: 5rem 5rem;
            height: 200px;
            width: 500px;
        }
       
      `}</style>
            </div>
        );
    }
}

export default App;
