import axios from 'axios';

import React, { Component } from 'react';
import { Button, Form, Container, Table } from 'react-bootstrap';

import API_ENDPOINT from "./api/endpoint"

class App extends Component {

    constructor(props) {
        super(props);
        this.state = { selectedFile: null, password: "", responseMsg: "" };
    }

    onPasswordChange = (e) => this.setState({password: e.target.value});

    onFileChange = (e) => this.setState({ selectedFile: e.target.files[0] });

    onFileUpload = () => {

        const formData = new FormData();

        formData.append(
            "uploadedFile",
            this.state.selectedFile,
            this.state.selectedFile.name,
        );

        formData.append("password", this.state.password)

        fetch(`${API_ENDPOINT}/update`, {method: "POST", body: formData})
        .then(res => res.json())
        .then(res => this.setState({responseMsg: res.msg}))
        .catch(err => this.setState({responseMsg: err}));
        
    };



    render() {

        return (
            <Container className="main">
                <Table striped bordered hover>
  <thead>
    <tr>
      <th>Variable</th>
      <th>Value</th>

    </tr>
  </thead>
  <tbody>
    <tr>
      <td>HCAO</td>
      <td>1.14</td>

    </tr>
    <tr>
      <td>HCAD</td>
      <td>0.986</td>

    </tr>
    <tr>
      <td>Last Updated</td>
      <td>12-19-2020 22:00:00 CST</td>
    </tr>
  </tbody>
</Table>

                
                <div><h1 className="mb-5">Update CSV</h1>
                    <input className="mb-5" type="file" accept=".csv" onChange={this.onFileChange} />
                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder={this.state.password} onChange={this.onPasswordChange} />
                    </Form.Group>
                    <Button className="mb-5" onClick={this.onFileUpload}>
                        Upload!
                </Button>
                <h1>{this.state.responseMsg}</h1>
                </div>


                <style global jsx>{`
        .main {
          position: relative;
          margin-top: 5rem;
          margin: auto;
          display: flex;
        }

        .current-status{
            border:5px solid black;
            border-radius: 2rem;
            padding: 5% 5%;
        }
       
      `}</style>
            </Container>
        );
    }
}

export default App;
