import axios from 'axios';

import React, { Component, useState, useEffect } from 'react';
import { Button, Form } from 'react-bootstrap';

import API_ENDPOINT from "./api/endpoint"

class App extends Component {

    constructor(props) {
        super(props);
        this.state = { selectedFile: null, password: "" };
    }

    onPasswordChange = (e) => this.setState({password: e.target.value});

    onFileChange = (e) => this.setState({ selectedFile: e.target.files[0] });

    onFileUpload = () => {

        const formData = new FormData();

        formData.append(
            "myFile",
            this.state.selectedFile,
            this.state.selectedFile.name,
        );

        axios.post("api/uploadfile", formData);
    };

    fileData = () => {

        if (this.state.selectedFile) {

            return (
                <div>
                    <h2>File Details:</h2>
                    <p>File Name: {this.state.selectedFile.name}</p>
                    <p>File Type: {this.state.selectedFile.type}</p>
                </div>
            );

        }

        else {
            return (
                <div>
                    <br />
                    <h4>Choose before Pressing the Upload button</h4>
                </div>
            );
        }
    };

    render() {

        return (
            <div className="main">
                <h1 className="mb-5">Update CSV</h1>
                <div>
                    <input className="mb-5" type="file" onChange={this.onFileChange} />
                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder={this.state.password} onChange={this.onPasswordChange} />
                    </Form.Group>
                    <Button onClick={this.onFileUpload}>
                        Upload!
                </Button>
                </div>


                <style global jsx>{`
        .main {
          position: absolute;
          margin-top: 5rem;
          left: 50%;
          transform: translate(-50%, 0);
          text-align:center;
        }
       
      `}</style>
            </div>
        );
    }
}

export default App;
