import axios from 'axios';

import React, { Component } from 'react';
import { Button, Form, Container, Table, FormGroup } from 'react-bootstrap';

import API_ENDPOINT from "./api/endpoint"

class App extends Component {

    constructor(props) {
        super(props);
        this.state = { selectedFile: null, password: "", responseMsg: "" };
    }

    onPasswordChange = (e) => this.setState({ password: e.target.value });

    onFileChange = (e) => this.setState({ selectedFile: e.target.files[0] });

    onFileUpload = () => {

        const formData = new FormData();

        formData.append(
            "uploadedFile",
            this.state.selectedFile,
            this.state.selectedFile.name,
        );

        formData.append("password", this.state.password)

        fetch(`${API_ENDPOINT}/update`, { method: "POST", body: formData })
            .then(res => res.json())
            .then(res => this.setState({ responseMsg: res.msg }))
            .catch(err => this.setState({ responseMsg: err }));

    };



    render() {

        return (
            <Container fluid={true}>
                <div className="flex-container">
                <Table className="flex-item" striped bordered hover>
                    <thead>
                        <tr>
                            <th>Variable</th>
                            <th>Value</th>

                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>HCAO</td>
                            <td>1.14</td>

                        </tr>
                        <tr>
                            <td>HCAD</td>
                            <td>0.986</td>
                        </tr>
                        <tr>
                            <td>Last Updated HC</td>
                            <td>12-19-2020 22:00:00 CST</td>
                        </tr>
                        <tr>
                            <td>Last Updated CSV</td>
                            <td>12-19-2020 22:00:00 CST</td>
                        </tr>
                    </tbody>
                </Table>

                <Form className="flex-item">
                    <h1 className="mb-5">Edit Settings</h1>

                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>HCAO</Form.Label>
                        <Form.Control type="text" placeholder={this.state.password} onChange={this.onPasswordChange} />
                    </Form.Group>
                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Updated CSV</Form.Label>
                        <input className="mb-5" type="file" accept=".csv" onChange={this.onFileChange} />
                    </Form.Group>
                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="password" placeholder={this.state.password} onChange={this.onPasswordChange} />
                    </Form.Group>
                    <Button className="mb-5" onClick={this.onFileUpload}>
                        Upload!
                    </Button>

                </Form>
                </div>

                

   
               
                <style global jsx>{`
        .flex-container {
          position: relative;
          margin-top: 10rem;
          left: 50%;
         
          display: flex;
          flex-direction: row;
          align-content: space-evenly;
          width: 90%;
        }

        .flex-item {
            height: 200px;
            width: 500px;
        }
       
      `}</style>
            </Container>
        );
    }
}

export default App;
